contracts/interfaces/IOracle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.13;

interface IOracle {

    struct Content {
        string contentType;
        string value;
    }

    struct Message {
        string role;
        Content [] content;
    }

    struct OpenAiRequest {
        // "gpt-4-turbo", "gpt-4-turbo-preview" or "gpt-3.5-turbo-1106"
        string model;
        // int -20 - 20, Mapped to float -2.0 - 2.0. If bigger than 20 then null
        int8 frequencyPenalty;
        // JSON string or empty string
        string logitBias;
        // 0 for null
        uint32 maxTokens;
        // int -20 - 20, Mapped to float -2.0 - 2.0. If bigger than 20 then null
        int8 presencePenalty;
        // JSON string or empty string
        string responseFormat;
        // 0 for null
        uint seed;
        // empty str for null
        string stop;
        // 0-20, > 20 for null
        uint temperature;
        // 0-100  percentage, > 100 for null
        uint topP;
        // JSON list for tools in OpenAI format, empty for null, names have to match the supported tools
        string tools;
        // "none", "auto" or empty str which defaults to auto on OpenAI side
        string toolChoice;
        string user;
    }

    struct OpenAiResponse {
        string id;

        // either content is an empty str or functionName and functionArguments
        string content;
        string functionName;
        string functionArguments;

        uint64 created;
        string model;
        string systemFingerprint;
        // kind of pointless since its always "chat.completion"?
        string object;

        uint32 completionTokens;
        uint32 promptTokens;
        uint32 totalTokens;
    }

    struct GroqRequest {
        // "llama3-8b-8192", "llama3-70b-8192", "mixtral-8x7b-32768" or "gemma-7b-it"
        string model;
        // int -20 - 20, Mapped to float -2.0 - 2.0. If bigger than 20 then null
        int8 frequencyPenalty;
        // JSON string or empty string
        string logitBias;
        // 0 for null
        uint32 maxTokens;
        // int -20 - 20, Mapped to float -2.0 - 2.0. If bigger than 20 then null
        int8 presencePenalty;
        // JSON string or empty string
        string responseFormat;
        // 0 for null
        uint seed;
        // empty str for null
        string stop;
        // 0-20, > 20 for null
        uint temperature;
        // 0-100  percentage, > 100 for null
        uint topP;
        string user;
    }

    struct GroqResponse {
        string id;

        string content;

        uint64 created;
        string model;
        string systemFingerprint;
        // kind of pointless since its always "chat.completion"?
        string object;

        uint32 completionTokens;
        uint32 promptTokens;
        uint32 totalTokens;
    }

    struct LlmRequest {
        // "claude-3-5-sonnet-20240620", "claude-3-opus-20240229", "claude-3-sonnet-20240229", "claude-3-haiku-20240307", "claude-2.1", "claude-2.0", "claude-instant-1.2"
        string model;
        // int -20 - 20, Mapped to float -2.0 - 2.0. If bigger than 20 then null
        int8 frequencyPenalty;
        // JSON string or empty string
        string logitBias;
        // 0 for null
        uint32 maxTokens;
        // int -20 - 20, Mapped to float -2.0 - 2.0. If bigger than 20 then null
        int8 presencePenalty;
        // JSON string or empty string
        string responseFormat;
        // 0 for null
        uint seed;
        // empty str for null
        string stop;
        // 0-20, > 20 for null
        uint temperature;
        // 0-100  percentage, > 100 for null
        uint topP;
        // JSON list for tools in OpenAI format, empty for null, names have to match the supported tools
        string tools;
        // "none", "auto" or empty str which defaults to auto on OpenAI side
        string toolChoice;
        string user;
    }

    struct LlmResponse {
        string id;

        // either content is an empty str or functionName and functionArguments
        string content;
        string functionName;
        string functionArguments;

        uint64 created;
        string model;
        string systemFingerprint;
        // kind of pointless since its always "chat.completion"?
        string object;

        uint32 completionTokens;
        uint32 promptTokens;
        uint32 totalTokens;
    }


    struct KnowledgeBaseQueryRequest {
        string cid;
        string query;
        uint32 num_documents;
    }

    function createLlmCall(
        uint promptId
    ) external returns (uint);

    function createLlmCall(
        uint promptId,
        LlmRequest memory request
    ) external returns (uint);

    function createGroqLlmCall(
        uint promptId,
        GroqRequest memory request
    ) external returns (uint);

    function createOpenAiLlmCall(
        uint promptId,
        OpenAiRequest memory request
    ) external returns (uint);

    function createFunctionCall(
        uint functionCallbackId,
        string memory functionType,
        string memory functionInput
    ) external returns (uint i);

    function createKnowledgeBaseQuery(
        uint kbQueryCallbackId,
        string memory cid,
        string memory query,
        uint32 num_documents
    ) external returns (uint i);
}

contracts/LeadAgent.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
// import "hardhat/console.sol";
import "./interfaces/IOracle.sol";
import "./interfaces/IOpenAiChatGpt.sol";
import "./factories/TechAgentFactory.sol";
import "./factories/SocialAgentFactory.sol";
import "./factories/DataAgentFactory.sol";

contract LeadAgent {

    string public prompt;

    struct Message {
        string role;
        string content;
    }

    struct AgentRun {
        address owner;
        address creator;
        address target;
        string targetFirstName;
        string targetFriend;
        string situation;
        string publicInfo;
        string privateInfo;
        string groupTitle;
        string groupImage;
        string groupId;
        Message[] commands;
        uint responsesCount;
        uint8 max_iterations;
        bool is_finished;
    }

    mapping(uint => AgentRun) public agentRuns;
    uint public agentRunCount;

    event AgentRunCreated(address indexed owner, uint indexed runId);

    address private owner;
    address public oracleAddress;

    event OracleAddressUpdated(address indexed newOracleAddress);

    IOracle.OpenAiRequest private config;

    IOpenAiChatGpt public techAgent;
    IOpenAiChatGpt public socialAgent;
    IOpenAiChatGpt public dataAgent;

    constructor(
        address initialOracleAddress,         
        string memory systemPrompt,
        address techAgentFactoryAddress,
        address socialAgentFactoryAddress,
        address dataAgentFactoryAddress
    ) {
        owner = msg.sender;
        oracleAddress = initialOracleAddress;
        prompt = systemPrompt;

        // Deploy sub-agents using factories
        techAgent = IOpenAiChatGpt(TechAgentFactory(techAgentFactoryAddress).createTechAgent(oracleAddress));
        socialAgent = IOpenAiChatGpt(SocialAgentFactory(socialAgentFactoryAddress).createSocialAgent(oracleAddress));
        dataAgent = IOpenAiChatGpt(DataAgentFactory(dataAgentFactoryAddress).createDataAgent(oracleAddress));

        config = IOracle.OpenAiRequest({
            model : "gpt-4-turbo-preview",
            frequencyPenalty : 21, // > 20 for null
            logitBias : "", // empty str for null
            maxTokens : 1000, // 0 for null
            presencePenalty : 21, // > 20 for null
            responseFormat : "{\"type\":\"text\"}",
            seed : 0, // null
            stop : "", // null
            temperature : 10, // Example temperature (scaled up, 10 means 1.0), > 20 means null
            topP : 101, // Percentage 0-100, > 100 means null
            tools : "[{\"type\":\"function\",\"function\":{\"name\":\"web_search\",\"description\":\"Search the internet\",\"parameters\":{\"type\":\"object\",\"properties\":{\"query\":{\"type\":\"string\",\"description\":\"Search query\"}},\"required\":[\"query\"]}}},{\"type\":\"function\",\"function\":{\"name\":\"image_generation\",\"description\":\"Generates an image using Dalle-2\",\"parameters\":{\"type\":\"object\",\"properties\":{\"prompt\":{\"type\":\"string\",\"description\":\"Dalle-2 prompt to generate an image\"}},\"required\":[\"prompt\"]}}}]",
            toolChoice : "auto", // "none" or "auto"
            user : "" // null
        });
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    modifier onlyOracle() {
        require(msg.sender == oracleAddress, "Caller is not oracle");
        _;
    }

    function setOracleAddress(address newOracleAddress) public onlyOwner {
        require(msg.sender == owner, "Caller is not the owner");
        oracleAddress = newOracleAddress;
        emit OracleAddressUpdated(newOracleAddress);
    }

    function runAgent(
        string memory query,
        uint8 max_iterations,
        string memory techAgentPrompt,
        string memory socialAgentPrompt,
        string memory dataAgentPrompt,
        address creator,
        address target,
        string memory targetFirstName,
        string memory targetFriend,
        string memory situation,
        string memory publicInfo,
        string memory privateInfo,
        string memory groupTitle,
        string memory groupImage,
        string memory groupId
    ) public returns (uint) {
        uint currentId = agentRunCount;
        AgentRun storage run = agentRuns[currentId];

        run.owner = msg.sender;
        run.creator = creator;
        run.target = target;
        run.targetFirstName = targetFirstName;
        run.targetFriend = targetFriend;
        run.situation = situation;
        run.publicInfo = publicInfo;
        run.privateInfo = privateInfo;
        run.groupTitle = groupTitle;
        run.groupImage = groupImage;
        run.groupId = groupId;
        run.is_finished = false;
        run.responsesCount = 0;
        run.max_iterations = max_iterations;

        // Initialize sub-agents with prompts
        techAgent.startChat(techAgentPrompt);
        socialAgent.startChat(socialAgentPrompt);
        dataAgent.startChat(dataAgentPrompt);

        Message memory systemMessage;
        systemMessage.content = prompt;
        systemMessage.role = "system";
        run.commands.push(systemMessage);

        Message memory newMessage;
        newMessage.content = query;
        newMessage.role = "user";
        run.commands.push(newMessage);

        agentRunCount = agentRunCount + 1;

        IOracle(oracleAddress).createOpenAiLlmCall(currentId, config);
        emit AgentRunCreated(run.owner, currentId);

        return currentId;
    }

    function onOracleOpenAiLlmResponse(
        uint runId,
        IOracle.OpenAiResponse memory response,
        string memory errorMessage
    ) public onlyOracle {
        AgentRun storage run = agentRuns[runId];

        // If there is an error message, add it to the commands and mark the run as finished
        if (!compareStrings(errorMessage, "")) {
            Message memory newMessage;
            newMessage.role = "assistant";
            newMessage.content = errorMessage;
            run.commands.push(newMessage);
            run.responsesCount++;
            run.is_finished = true;
            return;
        }

        // If there is a response content, add it to the commands
        if (!compareStrings(response.content, "")) {
            Message memory assistantMessage;
            assistantMessage.content = response.content;
            assistantMessage.role = "assistant";
            run.commands.push(assistantMessage);
            run.responsesCount++;

            if (startsWith(response.content, "TechAgent do:")) {
                techAgent.addMessage(response.content, runId);
            } else if (startsWith(response.content, "SocialAgent do:")) {
                socialAgent.addMessage(response.content, runId);
            } else if (startsWith(response.content, "DataAgent do:")) {
                dataAgent.addMessage(response.content, runId);
            }
        }

        // If there is a function name in the response, initiate a function call
        if (!compareStrings(response.functionName, "")) {
            IOracle(oracleAddress).createFunctionCall(runId, response.functionName, response.functionArguments);
            return;
        }

        // If the maximum number of iterations is reached, mark the run as finished
        if (run.responsesCount >= run.max_iterations) {
            run.is_finished = true;
            return;
        }

        // Do not call createOpenAiLlmCall here, wait for user input
    }

    function onOracleFunctionResponse(
        uint runId,
        string memory response,
        string memory errorMessage
    ) public onlyOracle {
        AgentRun storage run = agentRuns[runId];
        require(
            !run.is_finished, "Run is finished"
        );
        string memory result = response;
        if (!compareStrings(errorMessage, "")) {
            result = errorMessage;
        }
        Message memory newMessage;
        newMessage.role = "user";
        newMessage.content = result;
        run.commands.push(newMessage);
        run.responsesCount++;
        IOracle(oracleAddress).createOpenAiLlmCall(runId, config);
    }

    function getMessageHistoryContents(uint agentId) public view returns (string[] memory) {
        string[] memory commands = new string[](agentRuns[agentId].commands.length);
        for (uint i = 0; i < agentRuns[agentId].commands.length; i++) {
            commands[i] = agentRuns[agentId].commands[i].content;
        }
        return commands;
    }

    function getMessageHistoryRoles(uint agentId) public view returns (string[] memory) {
        string[] memory roles = new string[](agentRuns[agentId].commands.length);
        for (uint i = 0; i < agentRuns[agentId].commands.length; i++) {
            roles[i] = agentRuns[agentId].commands[i].role;
        }
        return roles;
    }

    function isRunFinished(uint runId) public view returns (bool) {
        return agentRuns[runId].is_finished;
    }

    function compareStrings(string memory a, string memory b) private pure returns (bool) {
        return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
    }

    // Add helper functions
    function startsWith(string memory str, string memory prefix) private pure returns (bool) {
        return keccak256(abi.encodePacked(substring(str, 0, bytes(prefix).length))) == keccak256(abi.encodePacked(prefix));
    }

    function substring(string memory str, uint startIndex, uint endIndex) private pure returns (string memory) {
        bytes memory strBytes = bytes(str);
        bytes memory result = new bytes(endIndex - startIndex);
        for (uint i = startIndex; i < endIndex; i++) {
            result[i - startIndex] = strBytes[i];
        }
        return string(result);
    }

    // @notice Adds a new message to an existing agent run
    // @param message The new message to add
    // @param runId The ID of the agent run
    function addMessage(string memory message, uint runId) public {
        AgentRun storage run = agentRuns[runId];
        require(!run.is_finished, "Run is finished");
        require(run.owner == msg.sender, "Only run owner can add commands");

        Message memory newMessage;
        newMessage.role = "user";
        newMessage.content = message;
        run.commands.push(newMessage);
        run.responsesCount++;

        // Continue the agent run by making another OpenAI LLM call
        IOracle(oracleAddress).createOpenAiLlmCall(runId, config);
    }

    // Add this new function to the LeadAgent contract
    function getAgentRunsForOwner(address _owner) public view returns (AgentRun[] memory) {
        uint count = 0;
        for (uint i = 0; i < agentRunCount; i++) {
            if (agentRuns[i].owner == _owner) {
                count++;
            }
        }

        AgentRun[] memory ownerRuns = new AgentRun[](count);
        uint index = 0;
        for (uint i = 0; i < agentRunCount; i++) {
            if (agentRuns[i].owner == _owner) {
                ownerRuns[index] = agentRuns[i];
                index++;
            }
        }

        return ownerRuns;
    }
}

contracts/PrevOpenAiChatGpt.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
// import "hardhat/console.sol";
import "./interfaces/IOracle.sol";

// @title PrevOpenAiChatGpt
// @notice This contract interacts with teeML oracle to handle chat interactions using the OpenAI model.
contract PrevOpenAiChatGpt {

    struct Message {
        string role;
        string content;
    }

    struct ChatRun {
        address owner;
        Message[] messages;
        uint messagesCount;
    }

    // @notice Mapping from chat ID to ChatRun
    mapping(uint => ChatRun) public chatRuns;
    uint private chatRunsCount;

    // @notice Event emitted when a new chat is created
    event ChatCreated(address indexed owner, uint indexed chatId);

    // @notice Address of the contract owner
    address private owner;

    // @notice Address of the oracle contract
    address public oracleAddress;

    // @notice Event emitted when the oracle address is updated
    event OracleAddressUpdated(address indexed newOracleAddress);

    // @notice Configuration for the OpenAI request
    IOracle.OpenAiRequest private config;

    // @param initialOracleAddress Initial address of the oracle contract
    constructor(address initialOracleAddress) {
        owner = msg.sender;
        oracleAddress = initialOracleAddress;
        chatRunsCount = 0;

        config = IOracle.OpenAiRequest({
            model : "gpt-4-turbo-preview",
            frequencyPenalty : 21, // > 20 for null
            logitBias : "", // empty str for null
            maxTokens : 1000, // 0 for null
            presencePenalty : 21, // > 20 for null
            responseFormat : "{\"type\":\"text\"}",
            seed : 0, // null
            stop : "", // null
            temperature : 10, // Example temperature (scaled up, 10 means 1.0), > 20 means null
            topP : 101, // Percentage 0-100, > 100 means null
            tools : "[{\"type\":\"function\",\"function\":{\"name\":\"web_search\",\"description\":\"Search the internet\",\"parameters\":{\"type\":\"object\",\"properties\":{\"query\":{\"type\":\"string\",\"description\":\"Search query\"}},\"required\":[\"query\"]}}},{\"type\":\"function\",\"function\":{\"name\":\"code_interpreter\",\"description\":\"Evaluates python code in a sandbox environment. The environment resets on every execution. You must send the whole script every time and print your outputs. Script should be pure python code that can be evaluated. It should be in python format NOT markdown. The code should NOT be wrapped in backticks. All python packages including requests, matplotlib, scipy, numpy, pandas, etc are available. Output can only be read from stdout, and stdin. Do not use things like plot.show() as it will not work. print() any output and results so you can capture the output.\",\"parameters\":{\"type\":\"object\",\"properties\":{\"code\":{\"type\":\"string\",\"description\":\"The pure python script to be evaluated. The contents will be in main.py. It should not be in markdown format.\"}},\"required\":[\"code\"]}}}]",
            toolChoice : "auto", // "none" or "auto"
            user : "" // null
        });
    }

    // @notice Ensures the caller is the contract owner
    modifier onlyOwner() {
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    // @notice Ensures the caller is the oracle contract
    modifier onlyOracle() {
        require(msg.sender == oracleAddress, "Caller is not oracle");
        _;
    }

    // @notice Updates the oracle address
    // @param newOracleAddress The new oracle address to set
    function setOracleAddress(address newOracleAddress) public onlyOwner {
        oracleAddress = newOracleAddress;
        emit OracleAddressUpdated(newOracleAddress);
    }

    // @notice Starts a new chat
    // @param message The initial message to start the chat with
    // @return The ID of the newly created chat
    function startChat(string memory message) public returns (uint) {
        ChatRun storage run = chatRuns[chatRunsCount];

        run.owner = msg.sender;
        Message memory newMessage = Message({
            role: "user",
            content: message
        });
        run.messages.push(newMessage);
        run.messagesCount = 1;

        uint currentId = chatRunsCount;
        chatRunsCount = chatRunsCount + 1;

        IOracle(oracleAddress).createOpenAiLlmCall(currentId, config);
        emit ChatCreated(msg.sender, currentId);

        return currentId;
    }

    // @notice Handles the response from the oracle for an OpenAI LLM call
    // @param runId The ID of the chat run
    // @param response The response from the oracle
    // @param errorMessage Any error message
    // @dev Called by teeML oracle
    function onOracleOpenAiLlmResponse(
        uint runId,
        IOracle.OpenAiResponse memory response,
        string memory errorMessage
    ) public onlyOracle {
        ChatRun storage run = chatRuns[runId];
        require(
            compareStrings(run.messages[run.messagesCount - 1].role, "user"),
            "No message to respond to"
        );

        if (!compareStrings(errorMessage, "")) {
            Message memory newMessage = Message({
                role: "assistant",
                content: errorMessage
            });
            run.messages.push(newMessage);
            run.messagesCount++;
        } else {
            if (compareStrings(response.content, "")) {
                IOracle(oracleAddress).createFunctionCall(runId, response.functionName, response.functionArguments);
            } else {
                Message memory newMessage = Message({
                    role: "assistant",
                    content: response.content
                });
                run.messages.push(newMessage);
                run.messagesCount++;
            }
        }
    }

    // @notice Handles the response from the oracle for a function call
    // @param runId The ID of the chat run
    // @param response The response from the oracle
    // @param errorMessage Any error message
    // @dev Called by teeML oracle
    function onOracleFunctionResponse(
        uint runId,
        string memory response,
        string memory errorMessage
    ) public onlyOracle {
        ChatRun storage run = chatRuns[runId];
        require(
            compareStrings(run.messages[run.messagesCount - 1].role, "user"),
            "No function to respond to"
        );
        if (compareStrings(errorMessage, "")) {
            Message memory newMessage = Message({
                role: "user",
                content: response
            });
            run.messages.push(newMessage);
            run.messagesCount++;
            IOracle(oracleAddress).createOpenAiLlmCall(runId, config);
        }
    }

    // @notice Adds a new message to an existing chat run
    // @param message The new message to add
    // @param runId The ID of the chat run
    function addMessage(string memory message, uint runId) public onlyOwner {
        ChatRun storage run = chatRuns[runId];
        require(
            compareStrings(run.messages[run.messagesCount - 1].role, "assistant"),
            "No response to previous message"
        );
        // require(
        //     run.owner == msg.sender, "Only chat owner can add messages"
        // );

        Message memory newMessage = Message({
            role: "user",
            content: message
        });
        run.messages.push(newMessage);
        run.messagesCount++;

        IOracle(oracleAddress).createOpenAiLlmCall(runId, config);
    }

    // @notice Retrieves the message contents of a chat run
    // @param chatId The ID of the chat run
    // @return An array of message contents
    function getMessageHistoryContents(uint chatId) public view returns (string[] memory) {
        ChatRun storage run = chatRuns[chatId];
        string[] memory contents = new string[](run.messagesCount);
        for (uint i = 0; i < run.messagesCount; i++) {
            contents[i] = run.messages[i].content;
        }
        return contents;
    }

    // @notice Retrieves the message roles of a chat run
    // @param chatId The ID of the chat run
    // @return An array of message roles
    function getMessageHistoryRoles(uint chatId) public view returns (string[] memory) {
        ChatRun storage run = chatRuns[chatId];
        string[] memory roles = new string[](run.messagesCount);
        for (uint i = 0; i < run.messagesCount; i++) {
            roles[i] = run.messages[i].role;
        }
        return roles;
    }

    // @notice Compares two strings for equality
    // @param a The first string
    // @param b The second string
    // @return True if the strings are equal, false otherwise
    function compareStrings(string memory a, string memory b) private pure returns (bool) {
        return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
    }
}

